    private void setExtendMotorPosition(double position) {
        extendMotor.getClosedLoopController().setSetpoint(position,ControlType.kMAXMotionPositionControl);
    }

    public STATUS getStatus() {
        return status;
    }

    public void setStatus(STATUS status) {
        bCommandComplete = false;
        switch (status) {
            case RETRACTED:
            targetStatus = STATUS.RETRACTED;
            status = STATUS.RETRACTING;
            //set extend motor to retract
            setExtendMotorPosition(status.getPosition());
             intakeMotor.setVoltage(0);
             bIntakeMotorRunning = false;
                break;
            case EXTENDING: //transition state
            targetStatus = STATUS.EXTENDED_STOPPED;
            setExtendMotorPosition(status.getPosition());
             intakeMotor.setVoltage(0);
             bIntakeMotorRunning = false;
                break;
            case RETRACTING://transition state
            targetStatus = STATUS.RETRACTED;    
            setExtendMotorPosition(status.getPosition());
            intakeMotor.setVoltage(0);
                bIntakeMotorRunning = false;
        
                break;
            case EXTENDED_STOPPED:
            targetStatus = STATUS.EXTENDED_STOPPED;
            status = STATUS.EXTENDING;
            setExtendMotorPosition(status.getPosition());
             intakeMotor.setVoltage(0);
             bIntakeMotorRunning = false;
                break;
            case EXTENDED_INTAKING:
            targetStatus = STATUS.EXTENDED_INTAKING;
            status = STATUS.EXTENDING;
            setExtendMotorPosition(status.getPosition());
             intakeMotor.setVoltage(0);
             bIntakeMotorRunning = false;

                break;
            case EXTENDED_OUTPUT:
            targetStatus = STATUS.EXTENDED_OUTPUT;
            status = STATUS.EXTENDING;
            setExtendMotorPosition(status.getPosition());
             intakeMotor.setVoltage(0);
                bIntakeMotorRunning = false;
                break;  
        
            default:
            targetStatus = STATUS.RETRACTED;
            status = STATUS.RETRACTING;
             intakeMotor.setVoltage(0);
                bIntakeMotorRunning = false;
                 setExtendMotorPosition(STATUS.RETRACTING.getPosition());
                break;
        }
        this.status = status;
    }

